    const history = useHistory();

    return (
      <div>
        <a href  = {`/product/${banner.productId}`}><img  style = {{height : '50vh' , objectFit : 'cover'}}  src = {banner.bannerUrl}/></a>
      </div>
    )
}

export default BannerCard;
