import './App.css';

import { useState } from 'react';
function App() {
  return (
    function App() {
      const [input,setInput]= useState(0);

      const RandomNumber =[(input)=>{
        for (var i = 1; i <= input; i++) {
          RandomNumber.push(Math.floor(Math.random() * input+1));
      }
      }];
      console.log(RandomNumber);
        return (
          <center>
       <div>
         RandomNumber:{RandomNumber}
            <h1> Number Filter </h1>
             <input type="number" name="input" placeholder="Enter the number of inputs" onChange={(e)=>setInput(e.target.value)}></input>  <br/>
             <button  onClick={RandomNumber} > Generate Random Number </button><br/>
             <button> Odd </button>
             <button> Even </button>
             <h1> Filtered Array </h1>
        </div>
        </center>
        );}
  );
}

export default App;
