import styled from 'styled-components';
   
export const Box = styled.div`
  background: black;
  position: absolute;
  bottom: 0;
  width: 100%;
`;
   
export const Container = styled.div`
    display: flex;
    flex-direction: column;
    width: 100%;
    background: rgb(4, 238, 20);
background: linear-gradient(90deg, rgba(12,237,26,1) 50%, rgba(115,223,10,0.6730042358740371) 22%, rgba(39,249,5,0.16880255520176823) 100%, rgba(1,255,0,1) 100%);

`
   
export const ColumnL = styled.div`
  display: flex;
  flex-direction: column;
  text-align: left;
  margin-left: 20px;
`;

export const ColumnC = styled.div`
  display: flex;
  flex-direction: column;
  text-align: left;
  margin-left: auto;
  margin-right: auto;
`;

export const ColumnR = styled.div`
  display: flex;
  flex-direction: column;
  text-align: left;
  margin-right: 10px;
`;
   
export const Row = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, 
                         minmax(185px, 1fr));
  grid-gap: 400px;
  @media (max-width: 1000px) {
    grid-template-columns: repeat(auto-fill, 
                           minmax(200px, 1fr));
  }
`;
   
export const FooterLink = styled.a`
color: #fff;
  margin-bottom: 10px;
  font-size: 20px;
  text-decoration: none;
   
  &:hover {
      color: green;
      transition: 200ms ease-in;
  }
`;
   
export const Heading = styled.p`
  font-size: 24px;
  color: #fff;
  margin-bottom: 30px;
  font-weight: bold;
  text-align: left;
  justify-content: center;
  align-items: center;
`;


export const Pip = styled.p`
color: #fff;
margin-bottom: 10px;
font-size: 20px;
text-decoration: none;
 
&:hover {
    color: green;
    transition: 200ms ease-in;
}
`;
